def pivot(data: "DataFrame", index=None, columns=None, values=None) -> "DataFrame":
    if columns is None: # Check if columns argument is provided
        raise ValueError("pivot() missing 1 required argument: 'columns'")

    if values is None:
        cols = [columns] if index is None else [index, columns]
        append = index is None
        indexed = data.set_index(cols, append=append)
    else:
        if index is None:
            index = data.index
        else:
            index = data[index]

        column_subset = data[columns]
        if isna(column_subset).any():  # Check for any NaN values in the column subset
            raise ValueError("pivot() column contains NaN values")

        index = MultiIndex.from_arrays([index, column_subset])
        values_subset = data[values]

        if is_list_like(values_subset) and not isinstance(values_subset, tuple):
            # Exclude tuple because it is seen as a single column name
            indexed = data._constructor(data[values].values, index=index, columns=values)
        else:
            indexed = data._constructor_sliced(data[values].values, index=index)

    return indexed.unstack(columns)
