def pivot_table(
    data,
    values=None,
    index=None,
    columns=None,
    aggfunc="mean",
    fill_value=None,
    margins=False,
    dropna=True,
    margins_name="All",
    observed=False,
) -> "DataFrame":
    index = _convert_by(index)
    columns = _convert_by(columns)

    keys = index + columns

    if isinstance(aggfunc, list):
        func_dict = {func.__name__: func for func in aggfunc}
        agged = data.pivot_table(
            values=values, index=index, columns=columns, aggfunc=func_dict
        )

    else:
        agged = data.pivot_table(
            values=values, index=index, columns=columns, aggfunc=aggfunc
        )

    if dropna and isinstance(agged, ABCDataFrame) and len(agged.columns):
        agged = agged.dropna(how="all")

    if fill_value is not None:
        agged = agged.fillna(fill_value)

    if margins:
        total = data.pivot_table(
            values=values, index=index, columns=columns, aggfunc=aggfunc
        )
        agged = agged.append(total)

    if len(index) == 0 and len(columns) > 0:
        agged = agged.T

    return agged
