async def request_body_to_args(
    required_params: List[ModelField],
    received_body: Optional[Union[Dict[str, Any], FormData]],
) -> Tuple[Dict[str, Any], List[ErrorWrapper]]:
    values = {}
    errors = []
    if received_body and isinstance(received_body, FormData):
        # Group the values of the same key into a list
        grouped_data = {}
        for key, value in received_body.items():
            grouped_data.setdefault(key, []).append(value)

        for field in required_params:
            value = grouped_data.get(field.alias)  # Get the list of values for the field
            if value is not None:
                # Use the list of values for validation
                v_, errors_ = field.validate(value, values, loc=("body", field.alias))
                if isinstance(errors_, ErrorWrapper):
                    errors.append(errors_)
                elif isinstance(errors_, list):
                    errors.extend(errors_)
                else:
                    values[field.name] = v_
            elif field.required:
                error_loc = ("body", field.alias)
                if PYDANTIC_1:
                    errors.append(ErrorWrapper(MissingError(), loc=error_loc))
                else:  
                    errors.append(ErrorWrapper(MissingError(), loc=error_loc, config=BaseConfig))
            else:
                values[field.name] = deepcopy(field.default)

    return values, errors
