async def request_body_to_args(
    required_params: List[ModelField],
    received_body: Optional[Union[Dict[str, Any], FormData]],
) -> Tuple[Dict[str, Any], List[ErrorWrapper]]:
    values = {}
    errors = []

    # Process each field in the required parameters list
    for field in required_params:
        field_name = field.alias
        value = None

        # Process multiple values for the same key
        if isinstance(field_info, params.Form) and isinstance(received_body, FormData):
            values[field.name] = received_body.getlist(field_name)
            continue

        # Process single value for the key
        value = received_body.get(field_name)
        if value is None:
            if field.required:
                if PYDANTIC_1:
                    errors.append(
                        ErrorWrapper(MissingError(), loc=("body", field.alias))
                    )
                else:
                    errors.append(
                        ErrorWrapper(
                            MissingError(), loc=("body", field.alias), config=BaseConfig
                        )
                    )
            else:
                values[field.name] = deepcopy(field.default)
            continue

        # Validate the value
        v_, errors_ = field.validate(value, values, loc=("body", field.alias))
        if isinstance(errors_, ErrorWrapper):
            errors.append(errors_)
        elif isinstance(errors_, list):
            errors.extend(errors_)
        else:
            values[field.name] = v_

    return values, errors
