async def request_body_to_args(
    required_params: List[ModelField],
    received_body: Optional[Union[Dict[str, Any], FormData]],
) -> Tuple[Dict[str, Any], List[ErrorWrapper]]:
    values = {}
    errors = []
    if received_body is not None:
        for field in required_params:
            field_alias = field.alias
            values_raw = received_body.getlist(field_alias) if isinstance(received_body, FormData) else received_body.get(field_alias)
            value = values_raw if len(values_raw) > 1 else values_raw[0] if len(values_raw) == 1 else None

            if value is None:
                if field.required:
                    errors.append(
                        ErrorWrapper(MissingError(), loc=("body", field_alias))
                    )
                else:
                    values[field.name] = deepcopy(field.default)
                continue

            if isinstance(field, ModelField) and hasattr(field, "validate"):
                v_, errors_ = field.validate(value, values, loc=("body", field_alias))
                if isinstance(errors_, ErrorWrapper):
                    errors.append(errors_)
                elif isinstance(errors_, list):
                    errors.extend(errors_)
                else:
                    values[field.name] = v_

    return values, errors
