def _get_grouper(
    obj,
    key=None,
    axis=0,
    level=None,
    sort=True,
    observed=False,
    mutated=False,
    validate=True,
):
    """
    create and return a BaseGrouper...
    (documentation remains the same)
    """
    group_axis = obj._get_axis(axis)

    # validate that the passed single level is compatible with the passed
    # axis of the object
    if level is not None:
        if isinstance(group_axis, MultiIndex):
            if is_list_like(level) and len(level) == 1:
                level = level[0]

            if key is None and is_scalar(level):
                key = group_axis.get_level_values(level)
                level = None
        else:
            if is_list_like(level):
                nlevels = len(level)
                if nlevels == 1:
                    level = level[0]
                elif nlevels == 0:
                    raise ValueError("No group keys passed!")
                else:
                    raise ValueError("multiple levels only valid with MultiIndex")

            if isinstance(level, str):
                if obj.index.name != level:
                    raise ValueError(
                        "level name {} is not the name of the index".format(level)
                    )
            elif not (-1 <= level < obj.index.nlevels):
                raise ValueError("Invalid level value for the index")

            level = None
            key = group_axis

    if isinstance(key, Grouper):
        binner, grouper, obj = key._get_grouper(obj, validate=False)
        if key.key is None:
            return grouper, [], obj
        else:
            return grouper, {key.key}, obj

    elif isinstance(key, BaseGrouper):
        return key, [], obj

    elif isinstance(key, tuple):
        msg = (
            "Interpreting tuple 'by' as a list of keys, rather than "
            "a single key. Use 'by=[...]' instead of 'by=(...)'. In "
            "the future, a tuple will always mean a single key."
        )
        warnings.warn(msg, FutureWarning, stacklevel=5)
        key = list(key)

    if not isinstance(key, list):
        keys = [key]
    else:
        keys = key

    groupings = []
    exclusions = []

    for gpr in keys:
        if not _is_label_like(gpr):
            try:
                obj._data.items.get_loc(gpr)
                in_axis, name, gpr = True, gpr, obj[gpr]
                exclusions.append(name)
            except Exception:
                in_axis, name = False, None
        else:
            in_axis, name = True, gpr.name
            exclusions.append(name)

        if is_categorical_dtype(gpr) and len(gpr) != obj.shape[axis]:
            raise ValueError(
                (
                    "Length of grouper ({len_gpr}) and axis ({len_axis})"
                    " must be the same length".format(
                        len_gpr=len(gpr), len_axis=obj.shape[axis]
                    )
                )
            )

        ping = Grouping(
            group_axis,
            gpr,
            obj=obj,
            name=name,
            level=level,
            sort=sort,
            observed=observed,
            in_axis=in_axis,
        )

        groupings.append(ping)

    if len(groupings) == 0 and len(obj):
        raise ValueError("No group keys passed!")
    elif len(groupings) == 0:
        groupings.append(Grouping(Index([], dtype="int"), np.array([], dtype=np.intp)))

    grouper = BaseGrouper(group_axis, groupings, sort=sort, mutated=mutated)
    return grouper, exclusions, obj
