def _get_grouper(
    obj: Any,
    key: Optional[Any] = None,
    axis: int = 0,
    level: Optional[Any] = None,
    sort: bool = True,
    observed: bool = False,
    mutated: bool = False,
    validate: bool = True) -> tuple:
    """
    create and return a BaseGrouper, which is an internal
    mapping of how to create the grouper indexers.
    This may be composed of multiple Grouping objects, indicating
    multiple groupers

    Groupers are ultimately index mappings. They can originate as:
    index mappings, keys to columns, functions, or Groupers

    Groupers enable local references to axis,level,sort, while
    the passed in axis, level, and sort are 'global'.

    This routine tries to figure out what the passing in references
    are and then creates a Grouping for each one, combined into
    a BaseGrouper.

    If observed & we have a categorical grouper, only show the observed
    values

    If validate, then check for key/level overlaps

    """
    group_axis = obj._get_axis(axis)

    if level is not None and isinstance(group_axis, MultiIndex):
        if is_list_like(level) and len(level) == 1:
            level = level[0]

        if key is None and is_scalar(level):
            key = group_axis.get_level_values(level)
            level = None
    else:
        if is_list_like(level):
            if len(level) == 1:
                level = level[0]
            elif len(level) == 0:
                raise ValueError("No group keys passed!")
            else:
                raise ValueError("multiple levels only valid with MultiIndex")

        if isinstance(level, str):
            if obj.index.name != level:
                raise ValueError(f"level name {level} is not the name of the index")
        elif not (-1 <= level <= 0):
            raise ValueError("level > 0 or level < -1 only valid with MultiIndex")

    is_tuple = isinstance(key, tuple)
    all_hashable = is_tuple and is_hashable(key)

    if is_tuple:
        if (all_hashable and key not in obj and set(key).issubset(obj)) or not all_hashable:
            msg = "Interpreting tuple 'by' as a list of keys, rather than a single key. Use 'by=[...]' instead of 'by=(...)'. In the future, a tuple will always mean a single key."
            warnings.warn(msg, FutureWarning, stacklevel=5)
            key = list(key)

    if not isinstance(key, list):
        keys = [key]
        match_axis_length = False
    else:
        keys = key
        match_axis_length = len(keys) == len(group_axis)

    any_callable = any(callable(g) or isinstance(g, dict) for g in keys)
    any_groupers = any(isinstance(g, Grouper) for g in keys)
    any_arraylike = any(isinstance(g, (list, tuple, Series, Index, np.ndarray)) for g in keys)

    if not any_callable and not any_arraylike and not any_groupers and match_axis_length and level is None:
        if isinstance(obj, DataFrame):
            all_in_columns_index = all(g in obj.columns or g in obj.index.names for g in keys)
        elif isinstance(obj, Series):
            all_in_columns_index = all(g in obj.index.names for g in keys)

        if not all_in_columns_index:
            keys = [com.asarray_tuplesafe(keys)]

    if isinstance(level, (tuple, list)):
        if key is None:
            keys = [None] * len(level)
        levels = level
    else:
        levels = [level] * len(keys)

    groupings = []
    exclusions = []

    for i, (gpr, level) in enumerate(zip(keys, levels)):
        if isinstance(gpr, str):
            if obj.index.name != gpr:
                raise ValueError(f"level name {gpr} is not the name of the index")

        elif not (-1 <= level <= 0):
            raise ValueError("level > 0 or level < -1 only valid with MultiIndex")

        ping = (
            Grouping(
                group_axis,
                gpr,
                obj=obj,
                level=level,
                sort=sort,
                observed=observed
            ) if not isinstance(gpr, Grouping) else gpr
        )

        groupings.append(ping)

    grouper = BaseGrouper(group_axis, groupings, sort=sort, mutated=mutated)
    return grouper, exclusions, obj
