def _get_grouper(
    obj,
    key=None,
    axis=0,
    level=None,
    sort=True,
    observed=False,
    mutated=False,
    validate=True,
):
    """
    create and return a BaseGrouper, which is an internal
    mapping of how to create the grouper indexers.
    This may be composed of multiple Grouping objects, indicating
    multiple groupers

    Groupers are ultimately index mappings. They can originate as:
    index mappings, keys to columns, functions, or Groupers

    Groupers enable local references to axis,level,sort, while
    the passed in axis, level, and sort are 'global'.

    This routine tries to figure out what the passing in references
    are and then creates a Grouping for each one, combined into
    a BaseGrouper.

    If observed & we have a categorical grouper, only show the observed
    values

    If validate, then check for key/level overlaps

    """
    group_axis = obj._get_axis(axis)

    # validate that the passed single level is compatible with the passed
    # axis of the object
    if level is not None:
        if isinstance(group_axis, MultiIndex):
            if is_list_like(level) and len(level) == 1:
                level = level[0]

            if key is None and is_scalar(level):
                key = group_axis.get_level_values(level)
                level = None
        else:
            if is_list_like(level):
                nlevels = len(level)
                if nlevels == 1:
                    level = level[0]
                elif nlevels == 0:
                    raise ValueError("No group keys passed!")
                else:
                    raise ValueError("multiple levels only valid with MultiIndex")

            if isinstance(level, str):
                if obj.index.name != level:
                    raise ValueError(
                        "level name {} is not the name of the index".format(level)
                    )
            elif level > 0 or level < -1:
                raise ValueError("level > 0 or level < -1 only valid with MultiIndex")

            level = None
            key = group_axis

    if isinstance(key, Grouper):
        binner, grouper, obj = key._get_grouper(obj, validate=False)
        if key.key is None:
            return grouper, [], obj
        else:
            return grouper, {key.key}, obj
    elif isinstance(key, BaseGrouper):
        return key, [], obj

    elif isinstance(key, (list, tuple)):
        keys = key
        match_axis_length = len(keys) == len(group_axis)
    else:
        keys = [key]
        match_axis_length = False

    # correct the logic depending on the data type of the 'key' parameter
    for i, k in enumerate(keys):
        if k not in set(group_axis):
            if isinstance(k, tuple) and all(isinstance(el, str) for el in k):
                # column names ('a', 'b') -> ['a', 'b']
                keys[i] = list(k)
            elif obj._is_level_reference(k):
                # treating k as a level reference
                keys[i] = None
            else:
                raise KeyError(k)

    groupings = []
    exclusions = []

    for k in keys:
        if k in obj:
            if validate:
                obj._check_label_or_level_ambiguity(k)
            in_axis, gpr, name = True, obj[k], k
            exclusions.append(name)
        else:
            if isinstance(k, tuple) and all(isinstance(el, str) for el in k):
                # column names ('a', 'b') -> ['a', 'b']
                k = list(k)
            if all(isinstance(e, str) for e in k):
                keys = [com.asarray_tuplesafe(k)]
            else:  # if k has a different data type, generate an exclusion and adjust group_axis accordingly
                exclusions.append(k)
                group_axis = group_axis.difference(set([k]))

        if isinstance(k, Grouping):
            groupings.append(k)
        else:
            ping = Grouping(
                group_axis, k, obj=obj, name=name, level=level, sort=sort, observed=observed, in_axis=in_axis
            )
            groupings.append(ping)

    if len(groupings) == 0 and len(obj):
        raise ValueError("No group keys passed!")

    grouper = BaseGrouper(group_axis, groupings, sort=sort, mutated=mutated)
    return grouper, exclusions, obj
