def _get_grouper(
    obj,
    key=None,
    axis=0,
    level=None,
    sort=True,
    observed=False,
    mutated=False,
    validate=True,
):
    group_axis = obj._get_axis(axis)

    if level is not None:
        if isinstance(group_axis, MultiIndex):
            if is_list_like(level) and len(level) == 1:
                level = level[0]

            if key is None and is_scalar(level):
                key = group_axis.get_level_values(level)
                level = None
        else:
            if is_list_like(level):
                nlevels = len(level)
                if nlevels == 1:
                    level = level[0]
                elif nlevels == 0:
                    raise ValueError("No group keys passed!")
                else:
                    raise ValueError("multiple levels only valid with MultiIndex")

            if isinstance(level, str):
                if obj.index.name != level:
                    raise ValueError(
                        "level name {} is not the name of the index".format(level)
                    )
            elif level > 0 or level < -1:
                raise ValueError("level > 0 or level < -1 only valid with MultiIndex")

            level = None
            key = group_axis

    if isinstance(key, Grouper):
        binner, grouper, obj = key._get_grouper(obj, validate=False)
        if key.key is None:
            return grouper, [], obj
        else:
            return grouper, {key.key}, obj

    elif isinstance(key, BaseGrouper):
        return key, [], obj

    is_tuple = isinstance(key, tuple)
    all_hashable = is_tuple and all(is_hashable(k) for k in key)

    if is_tuple:
        if all_hashable and key not in obj and set(key).issubset(obj):
            warnings.warn(
                "Interpreting tuple 'by' as a list of keys, rather than a single key. Use 'by=[...]' instead of 'by=(...)'. In the future, a tuple will always mean a single key.",
                FutureWarning,
                stacklevel=5
            )
            key = list(key)

    if not isinstance(key, list):
        key = [key]

    match_axis_length = len(key) == len(group_axis)

    for k in key:
        if not is_hashable(k) and (k not in obj or k not in obj.index.names):
            raise KeyError(k)

    any_callable = any(callable(k) or isinstance(k, dict) for k in key)
    any_groupers = any(isinstance(k, Grouper) for k in key)
    any_arraylike = any(isinstance(k, (list, tuple, Series, Index, np.ndarray)) for k in key)

    if not any_callable and not any_arraylike and not any_groupers and match_axis_length and level is None:
        if isinstance(obj, DataFrame):
            all_in_columns_index = all(
                k in obj.columns or k in obj.index.names for k in key
            )
        elif isinstance(obj, Series):
            all_in_columns_index = all(k in obj.index.names for k in key)

        if not all_in_columns_index:
            key = [com.asarray_tuplesafe(key)]

    if isinstance(level, (tuple, list)):
        if key is None:
            key = [None] * len(level)
        levels = level
    else:
        levels = [level] * len(key)

    groupings = []
    exclusions = []

    for i, (k, l) in enumerate(zip(key, levels)):
        if not is_hashable(k) and k not in obj:
            raise KeyError(k)

        if is_categorical_dtype(k) and len(k) != obj.shape[axis]:
            raise ValueError(
                "Length of grouper ({len_gpr}) and axis ({len_axis}) must be same length".format(
                    len_gpr=len(k), len_axis=obj.shape[axis]
                )
            )

        ping = (
            Grouping(
                group_axis,
                k,
                obj=obj,
                name=None if isinstance(k, Grouper) else k,
                level=l,
                sort=sort,
                observed=observed,
                in_axis=True,
            )
        )
        groupings.append(ping)

    if len(groupings) == 0 and len(obj):
        raise ValueError("No group keys passed!")
    elif len(groupings) == 0:
        groupings.append(Grouping(Index([], dtype="int"), np.array([], dtype=np.intp)))

    grouper = BaseGrouper(group_axis, groupings, sort=sort, mutated=mutated)
    return grouper, exclusions, obj
