def _get_grouper(
    obj,
    key=None,
    axis=0,
    level=None,
    sort=True,
    observed=False,
    mutated=False,
    validate=True,
):
    # Check for MultiIndex and handle level and key inputs accordingly
    group_axis = obj._get_axis(axis)

    if level is not None:
        if isinstance(group_axis, MultiIndex):
            # If level is iterable and has only one element, use that as level
            if is_list_like(level) and len(level) == 1:
                level = level[0]

            if key is None and is_scalar(level):
                key = group_axis.get_level_values(level)
                level = None
        else:
            # Allow level to be a length-one list-like object
            if is_list_like(level):
                nlevels = len(level)
                if nlevels == 1:
                    level = level[0]
                elif nlevels == 0:
                    raise ValueError("No group keys passed!")
                else:
                    raise ValueError("Multiple levels only valid with a MultiIndex")

            # Handle level based on its type
            if isinstance(level, str):
                if obj.index.name != level:
                    raise ValueError(f"Level name '{level}' is not the name of the index")
            elif level > 0 or level < -1:
                raise ValueError("Level > 0 or level < -1 only valid with MultiIndex")

            level = None
            key = group_axis
