def _get_grouper(
    obj,
    key=None,
    axis=0,
    level=None,
    sort=True,
    observed=False,
    mutated=False,
    validate=True,
):
    group_axis = obj._get_axis(axis)

    if level is not None:
        if isinstance(group_axis, MultiIndex):
            if is_list_like(level) and len(level) == 1:
                level = level[0]

            if key is None and is_scalar(level):
                key = group_axis.get_level_values(level)
                level = None
        else:
            if is_list_like(level):
                nlevels = len(level)
                if nlevels == 1:
                    level = level[0]
                elif nlevels == 0:
                    raise ValueError("No group keys passed!")
                else:
                    raise ValueError("multiple levels only valid with MultiIndex")

            if isinstance(level, str):
                if obj.index.name != level:
                    raise ValueError(
                        "level name {} is not the name of the index".format(level)
                    )
            elif level > 0 or level < -1:
                raise ValueError("level > 0 or level < -1 only valid with MultiIndex")

            level = None
            key = group_axis

    if isinstance(key, (Grouper, BaseGrouper)):
        return key, [], obj

    is_tuple = isinstance(key, tuple)
    all_hashable = is_tuple and is_hashable(key)

    if is_tuple:
        if (all_hashable and key not in obj and set(key).issubset(obj)) or not all_hashable:
            msg = (
                "Interpreting tuple 'by' as a list of keys, rather than "
                "a single key. Use 'by=[...]' instead of 'by=(...)'. In "
                "the future, a tuple will always mean a single key."
            )
            warnings.warn(msg, FutureWarning, stacklevel=5)
            key = list(key)

    if not isinstance(key, list):
            keys = [key]
            match_axis_length = False
    else:
        keys = key
        match_axis_length = len(keys) == len(group_axis)

    any_callable = any(callable(g) or isinstance(g, dict) for g in keys)
    any_groupers = any(isinstance(g, (Grouper, BaseGrouper)) for g in keys)
    any_arraylike = any(is_list_like(g) for g in keys)

    if (not any_callable and not any_arraylike and not any_groupers
        and match_axis_length and level is None):

        all_in_columns_index = all(
            g in obj.columns or g in obj.index.names for g in keys
        )
        if not all_in_columns_index:
            keys = [com.asarray_tuplesafe(keys)]

    if isinstance(level, (tuple, list)):
        if key is None:
            keys = [None] * len(level)

    groupings = []
    exclusions = []

    for i, (gpr, level) in enumerate(zip(keys, levels)):
        if (gpr in obj.index or gpr in obj and not isinstance(gpr, (Grouper, BaseGrouper))):
            in_axis, name, gpr = True, gpr, obj[gpr]
            exclusions.append(name)
        else:
            raise KeyError(gpr)

        if is_categorical_dtype(gpr) and len(gpr) != obj.shape[axis]:
            raise ValueError(
                f"Length of grouper ({len_gpr}) and axis ({obj.shape[axis]})"
                " must be same length"
            )

        # create the Grouping
        # allow us to passing the actual Grouping as the gpr
        ping = Grouping(
            group_axis, gpr, obj=obj, name=name, level=level, sort=sort,
            observed=observed, in_axis=in_axis
        ) if not isinstance(gpr, Grouping) else gpr

        groupings.append(ping)

    if len(groupings) == 0 and len(obj):
        raise ValueError("No group keys passed!")
    else:
        grouper = BaseGrouper(group_axis, groupings, sort=sort, mutated=mutated)
        return grouper, exclusions, obj
