def apply(self, other):
        if isinstance(other, datetime):
            n = self.n
            businesshours = sum(
                self._get_business_hours_by_sec(st, en)
                for st, en in zip(self.start, self.end)
            )
            total_seconds = n * 60 * 60
            business_days, remainder_seconds = divmod(abs(total_seconds), businesshours)
            if n < 0:
                business_days, remainder_seconds = -business_days, -remainder_seconds

            adjusted_datetime = other + timedelta(days=business_days)
            adjusted_datetime = self._next_opening_time(adjusted_datetime)
            adjusted_datetime += timedelta(seconds=remainder_seconds)

            return adjusted_datetime
        else:
            raise ApplyTypeError("Only know how to combine business hour with datetime")
