def _get_grouper(
    obj,
    key=None,
    axis=0,
    level=None,
    sort=True,
    observed=False,
    mutated=False,
    validate=True,
):
    group_axis = obj._get_axis(axis)

    if level is not None:
        if isinstance(group_axis, MultiIndex):
            if is_list_like(level) and len(level) == 1:
                level = level[0]

            if key is None and is_scalar(level):
                key = level
                level = None
        else:
            if is_list_like(level):
                nlevels = len(level)
                if nlevels == 1:
                    level = level[0]
                elif nlevels == 0:
                    raise ValueError("No group keys passed!")
                else:
                    raise ValueError("multiple levels only valid with MultiIndex")

            if isinstance(level, str):
                if obj.columns.name != level:
                    raise ValueError(
                        f"level name {level} is not the name of the columns"
                    )
            else:
                raise ValueError("Grouping by columns must use column names as keys")

    if isinstance(key, str):
        keys = [key]
    elif isinstance(key, list):
        keys = key
    else:
        keys = [key]

    groupings = []
    exclusions = []

    for key in keys:
        if key in obj:
            df_or_series = obj[key]
            in_axis = True
            name = key
        else:
            in_axis = False
            name = None
            df_or_series = None

        if is_categorical_dtype(df_or_series) and len(df_or_series) != obj.shape[axis]:
            raise ValueError(
                f"Length of grouper ({len(df_or_series)}) and axis ({obj.shape[axis]}) must be the same length"
            )

        ping = Grouping(
            group_axis,
            df_or_series,
            obj=obj,
            name=name,
            level=level,
            sort=sort,
            observed=observed,
            in_axis=in_axis,
        )
        groupings.append(ping)

    if len(groupings) == 0 and len(obj):
        raise ValueError("No group keys passed!")
    elif len(groupings) == 0:
        groupings.append(Grouping(Index([], dtype="int"), np.array([], dtype=np.intp)))

    grouper = BaseGrouper(group_axis, groupings, sort=sort, mutated=mutated)
    return grouper, exclusions, obj
