def _get_grouper(obj, key=None, axis=0, level=None, sort=True, observed=False, mutated=False, validate=True):
    group_axis = obj._get_axis(axis)

    if level is not None:
        if isinstance(group_axis, MultiIndex):
            if is_list_like(level) and len(level) == 1:
                level = level[0]
            if key is None and is_scalar(level):
                key = group_axis.get_level_values(level)
                level = None
        else:
            if is_list_like(level):
                nlevels = len(level)
                if nlevels == 1:
                    level = level[0]
                elif nlevels == 0:
                    raise ValueError("No group keys passed!")
                else:
                    raise ValueError("multiple levels only valid with MultiIndex")

            if isinstance(level, str):
                if level not in obj.columns.names:
                    raise ValueError(f"level name '{level}' is not the name of the columns")
            elif isinstance(level, int) and not (-1 <= level < len(group_axis.levels)):
                raise ValueError("Invalid level for MultiIndex columns")

            level = None
            key = group_axis

    if isinstance(key, (Grouper, BaseGrouper)):
        return key, [], obj

    is_tuple = isinstance(key, tuple)
    all_hashable = is_tuple and all(is_hashable(k) for k in key)

    if is_tuple:
        if (all_hashable and key not in obj and set(key).issubset(obj)) or not all_hashable:
            warnings.warn(
                "Interpreting tuple 'by' as a list of keys, rather than a single key. Use 'by=[...]' instead of 'by=(...)'.",
                FutureWarning,
                stacklevel=5,
            )
            key = list(key)

    if not isinstance(key, list):
        keys = [key]
        match_axis_length = False
    else:
        keys = key
        match_axis_length = len(keys) == len(group_axis)

    any_callable = any(callable(g) or isinstance(g, dict) for g in keys)
    any_groupers = any(isinstance(g, Grouper) for g in keys)
    any_arraylike = any(isinstance(g, (list, tuple, Series, Index, np.ndarray)) for g in keys)

    if not any_callable and not any_arraylike and not any_groupers and match_axis_length and level is None:
        if isinstance(obj, DataFrame):
            all_in_columns_index = all(g in obj.columns.names for g in keys)
        elif isinstance(obj, Series):
            all_in_columns_index = all(g in obj.index.names for g in keys)
        if not all_in_columns_index:
            keys = [keys]

    if isinstance(level, (tuple, list)):
        if key is None:
            keys = [None] * len(level)
        levels = level
    else:
        levels = [level] * len(keys)

    groupings = []
    exclusions = []

    for i, (gpr, level) in enumerate(zip(keys, levels)):
        if isinstance(gpr, str) and gpr in obj:
            name = gpr
            in_axis = True
            obj._check_label_or_level_ambiguity(gpr)
        elif isinstance(gpr, int) and obj._is_level_reference(gpr):
            name = None
            in_axis = False
        else:
            raise KeyError(gpr)

        # create the Grouping
        ping = Grouping(
            group_axis,
            gpr,
            obj=obj,
            name=name,
            level=level,
            sort=sort,
            observed=observed,
            in_axis=in_axis,
        )

        groupings.append(ping)

    if len(groupings) == 0 and len(obj):
        raise ValueError("No group keys passed!")
    elif len(groupings) == 0:
        groupings.append(Grouping(Index([], dtype="int"), np.array([], dtype=np.intp)))

    grouper = BaseGrouper(group_axis, groupings, sort=sort, mutated=mutated)
    return grouper, exclusions, obj
