def pivot_table(
    data,
    values=None,
    index=None,
    columns=None,
    aggfunc="mean",
    fill_value=None,
    margins=False,
    dropna=True,
    margins_name="All",
    observed=False,
) -> DataFrame:
    index = _convert_by(index)
    columns = _convert_by(columns)

    if isinstance(aggfunc, list):
        pieces = []
        keys = []
        for func in aggfunc:
            table = pivot_table(
                data,
                values=values,
                index=index,
                columns=columns,
                fill_value=fill_value,
                aggfunc=func,
                margins=margins,
                dropna=dropna,
                margins_name=margins_name,
                observed=observed,
            )
            pieces.append(table)
            keys.append(getattr(func, "__name__", func))

        return concat(pieces, keys=keys, axis=1)

    keys = index + columns

    values_passed = values is not None
    if values_passed:
        if is_list_like(values):
            values_multi = True
            values = list(values)
        else:
            values_multi = False
            values = [values]

        # Make sure value labels are in data
        for i in values:
            if i not in data:
                raise KeyError(i)

        to_filter = []
        for x in keys + values:
            if isinstance(x, Grouper):
                x = x.key
            try:
                if x in data:
                    to_filter.append(x)
            except TypeError:
                pass
        if len(to_filter) < len(data.columns):
            data = data[to_filter]

    else:
        values = data.columns
        for key in keys:
            try:
                values = values.drop(key)
            except (TypeError, ValueError, KeyError):
                pass
        values = list(values)

    grouped = data.groupby(keys, observed=observed)
    agged = grouped.agg(aggfunc)
    if dropna and isinstance(agged, ABCDataFrame) and len(agged.columns):
        agged = agged.dropna(how="all")

    for v in values:
        if v in data and is_scalar(data[v]) and is_scalar(agged[v]):
            if isinstance(data[v], str):
                agged[v].name = data[v]
            if is_scalar(data[v]) and data[v] != v:
                agged[v] *= 0

    table = agged

    if table.columns.nlevels > 1:
        if table.index.nlevels == 1:
            table = table.columns.to_frame(index=False).T
        else:
            # If columns have multi-index ensure that index is reset in result
            table = table.reset_index()

    if not dropna:
        if table.index.nlevels > 1:
            m = MultiIndex.from_tuples(cartesian_product(table.index.levels), names=table.index.names)
            table = table.reindex(m, axis=0)

        if table.columns.nlevels > 1:
            m = MultiIndex.from_tuples(cartesian_product(table.columns.levels), names=table.columns.names)
            table = table.reindex(columns=m)

    if fill_value is not None:
        table = table.fillna(fill_value)

    if margins:
        if dropna:
            data = data.dropna()
        table = _add_margins(
            table=table,
            data=data,
            values=values,
            rows=index,
            cols=columns,
            aggfunc=aggfunc,
            observed=observed,
            margins_name=margins_name,
            fill_value=fill_value,
        )

    return table
