def _partially_consume_prefix(self, prefix, column):

    lines = []
    current_line = ""
    current_column = 0
    wait_for_nl = False
    for char in prefix:
        if char == '\t':
            current_column += 4 - (current_column % 4)
        elif char == ' ':
            current_column += 1
        elif char == '\n':
            lines.append(current_line + char)
            current_line = ""
            current_column = 0
            wait_for_nl = False
            continue
        else:
            current_line += char
            current_column += 1
        if wait_for_nl:
            if current_column >= column and current_line.strip():
                res = ''.join(lines)
                return res, prefix[len(res):]
            wait_for_nl = False
        elif char == ' ' or char == '\t':
            wait_for_nl = True
    return ''.join(lines), current_line
