def _partially_consume_prefix(self, prefix, column):

    lines = []
    current_line = ""
    current_column = 0
    for char in prefix:
        if current_line.strip() and current_column < column and not char.isspace():
            res = ''.join(lines)
            return res, prefix[len(res):]

        if char == '\n':
            res = ''.join(lines)
            return res, prefix[len(res):]

        current_line += char
        if char == ' ':
            current_column += 1
        elif char == '\t':
            current_column += 4

        lines.append(current_line)
        current_line = ""
        current_column = 0

    return ''.join(lines), current_line
