def _convert_key(self, key, is_setter: bool = False):

    if is_setter:
        return list(key)

    index = self.obj.axes[0]
    key_list = []
    for i in key:
        if not isinstance(i, type(index)):
            i = index._coerce_scalar_to_index(i)  # Coerce a scalar value to the index type
        key_list.append(i)

    return tuple(key_list)
