def _htmlentity_transform(entity):
    # Known non-numeric HTML entity
    import html
    if entity in html.entities.html5:
        return html.entities.html5[entity]

    num = entity.lstrip('#')
    if entity.startswith('x'):
        return chr(int(num, 16))
    else:
        return chr(int(num))
