def _htmlentity_transform(entity):
    import re
    """
    Transforms an HTML entity to a character.
    """

    if entity.startswith("&#") or entity.startswith("&#x"):
        numstr = entity[2:-1]
        base = 16 if entity.startswith("&#x") else 10
        return chr(int(numstr, base))
    else:
        return entity
