def cache_readonly(func):
    attr_name = f'_{func.__name__}'

    @property
    def wrapper(self):
        if getattr(self, attr_name, None) is None:
            setattr(self, attr_name, func(self))
        return getattr(self, attr_name)

    return wrapper

