    """
    Require they keys to be the same type as the index. (so we don't
    fallback)
    """
    # allow arbitrary setting
    if is_setter:
        return list(key)

    idx_type = type(self.obj.index)
    if idx_type is pd.Float64Index:
        key = self.obj.index._validate_indexer("getitem", key)
