def _get_merge_keys(self):

    left_join_keys, right_join_keys, join_names = super()._get_merge_keys()

    # validate index types are the same
    for i, (lk, rk) in enumerate(zip(left_join_keys, right_join_keys)):
        if not is_dtype_equal(lk.dtype, rk.dtype):
            if is_categorical_dtype(lk.dtype) and is_categorical_dtype(rk.dtype):
                # The generic error message is confusing for categoricals.
                #
                # In this function, the join keys include both the original
                # ones of the merge_asof() call, and also the keys passed
                # to its by= argument. Unordered but equal categories
                # are not supported for the former, but will fail
                # later with a ValueError, so we don't *need* to check
                # for them here.
                msg = (
                    "incompatible merge keys [{i}] {lkdtype} and "
                    "{rkdtype}, both sides category, but not equal ones".format(
                        i=i, lkdtype=repr(lk.dtype), rkdtype=repr(rk.dtype)
                    )
                )
            else:
                msg = (
                    "incompatible merge keys [{i}] {lkdtype} and "
                    "{rkdtype}, must be the same type".format(
                        i=i, lkdtype=repr(lk.dtype), rkdtype=repr(rk.dtype)
                    )
                )
            raise MergeError(msg)

    # validate tolerance; datetime.timedelta or Timedelta if we have a DTI
    if self.tolerance is not None:
        if self.left_index:
            lt = self.left.index
        else:
            lt = left_join_keys[-1]

        if is_datetimelike(lt) and not isinstance(self.tolerance, datetime.timedelta):
            raise MergeError("incompatible tolerance {tolerance}, must be compatible with type {lkdtype}".format(
                tolerance=type(self.tolerance), lkdtype=repr(lt.dtype)
            ))
        if is_int64_dtype(lt) and not is_integer(self.tolerance):
            raise MergeError("incompatible tolerance {tolerance}, must be compatible with type {lkdtype}".format(
                tolerance=type(self.tolerance), lkdtype=repr(lt.dtype)
            ))
        if is_float_dtype(lt) and not is_number(self.tolerance):
            raise MergeError("incompatible tolerance {tolerance}, must be compatible with type {lkdtype}".format(
                tolerance=type(self.tolerance), lkdtype=repr(lt.dtype)
            ))
        if self.tolerance < 0:
            raise MergeError("tolerance must be positive")
    else:
        if not is_integer(lt) and not is_float_dtype(lt) and not is_datetimelike(lt):
            raise MergeError("key must be integer, timestamp, or float")

    # validate allow_exact_matches
    if not isinstance(self.allow_exact_matches, bool):
        msg = "allow_exact_matches must be boolean, passed {passed}".format(passed=self.allow_exact_matches)
        raise MergeError(msg)

    return left_join_keys, right_join_keys, join_names
