def add(self, layer):
    if not isinstance(layer, Layer):
        raise TypeError('The added layer must be an instance of class Layer. Found: ' + str(layer))
    self.built = False
    if not self._layers:
        # First layer in model: check that it is an input layer.
        if not isinstance(layer, InputLayer):
            # Create an input tensor and call `layer` on the input tensor.
            # First, we need to infer the expected input shape and dtype.
            first_layer = layer
            if isinstance(layer, (Model, Sequential)):
                # We were passed a model as the first layer.
                # This requires a specific way to figure out the input shape and dtype.
                if not layer.layers:
                    raise ValueError('Cannot add an empty model to a `Sequential` model.')
                # In case of nested models: recover the first layer
                # of the deepest model to infer input shape and dtype.
                first_layer = layer.layers[0]
                while isinstance(first_layer, (Model, Sequential)):
                    first_layer = first_layer.layers[0]
                if isinstance(first_layer, InputLayer):
                    # Corner case where the user passes an InputLayer via `add`.
                    assert len(first_layer._inbound_nodes[-1].output_tensors) == 1
                    set_inputs = True
                else:
                    batch_shape = first_layer.batch_input_shape
                    dtype = first_layer.dtype
                    # Instantiate the input layer.
                    x = Input(batch_shape=batch_shape, dtype=dtype, name=first_layer.name + '_input')
                    # This will build the current layer and create the node connecting the current layer
                    # to the input layer we just created.
                    layer(x)
                    set_inputs = True
    else:
        output_tensor = layer(self.outputs[0])
        if isinstance(output_tensor, list):
            raise TypeError('All layers in a Sequential model should have a single output tensor. For multi-output layers, use the functional API.')
        self.outputs = [output_tensor]
    if self.inputs:
        self.build()
    else:
        self._layers.append(layer)
