            layer(x)
            set_inputs = True

    if set_inputs:
        if len(layer._inbound_nodes[-1].output_tensors) != 1:
            raise ValueError('All layers in a Sequential model should have a single output tensor.')
        self.outputs = [layer._inbound_nodes[-1].output_tensors[0]]
