def nonsingular(vmin, vmax, expander=0.001, tiny=1e-15, increasing=True):
    # Check if inputs are finite
    if (not np.isfinite(vmin)) or (not np.isfinite(vmax)):
        return -expander, expander

    # Ensure vmin <= vmax if increasing is True
    if increasing:
        vmin, vmax = min(vmin, vmax), max(vmin, vmax)

    # Calculate maxabsvalue
    maxabsvalue = max(abs(vmin), abs(vmax))

    # Check if the interval is too small and expand if necessary
    if vmax - vmin <= maxabsvalue * tiny:
        if maxabsvalue < 1/twenty_thirds * np.finfo(float).tiny:
            vmin = -expander
            vmax = expander
        else:
            new_vmin = vmin - expander * maxabsvalue / 2
            new_vmax = vmax + expander * maxabsvalue / 2
            if new_vmin == 0 and new_vmax == 0:
                vmin = -expander
                vmax = expander
            else:
                vmin = new_vmin
                vmax = new_vmax

    # Swap vmin and vmax if necessary
    if not increasing:
        vmin, vmax = min(vmin, vmax), max(vmin, vmax)

    return vmin, vmax
