def _get_grouper(
    obj,
    key=None,
    axis=0,
    level=None,
    sort=True,
    observed=False,
    mutated=False,
    validate=True,
):
    group_axis = obj._get_axis(axis)

    if level is not None:
        if isinstance(group_axis, MultiIndex):
            if is_list_like(level) and len(level) == 1:
                level = level[0]

            if key is None and is_scalar(level):
                key = group_axis.get_level_values(level)
                level = None

        else:
            if is_list_like(level):
                nlevels = len(level)
                if nlevels == 1:
                    level = level[0]
                elif nlevels == 0:
                    raise ValueError("No group keys passed!")
                else:
                    raise ValueError("multiple levels only valid with MultiIndex")

            if isinstance(level, str):
                if obj.index.name != level:
                    raise ValueError(
                        "level name {} is not the name of the index".format(level)
                    )
            elif level > 0 or level < -1:
                raise ValueError("level > 0 or level < -1 only valid with MultiIndex")

            level = None
            key = group_axis

    if isinstance(key, Grouper):
        binner, grouper, obj = key._get_grouper(obj, validate=False)
        if key.key is None:
            return grouper, [], obj
        else:
            return grouper, {key.key}, obj
    elif isinstance(key, BaseGrouper):
        return key, [], obj

    is_tuple = isinstance(key, tuple)
    all_hashable = is_tuple and all(map(is_hashable, key))

    if is_tuple:
        if (all_hashable and key not in obj and set(key).issubset(obj)) or not all_hashable:
            msg = "Interpreting tuple 'by' as a list of keys, rather than a single key. Use 'by=[...]' instead of 'by=(...)'. In the future, a tuple will always mean a single key."
            warnings.warn(msg, FutureWarning, stacklevel=5)
            key = list(key)

    if not isinstance(key, list):
        keys = [key]
        match_axis_length = False
    else:
        keys = key
        match_axis_length = len(keys) == len(group_axis)

    any_callable = any(callable(g) or isinstance(g, dict) for g in keys)
    any_groupers = any(isinstance(g, Grouper) for g in keys)
    any_arraylike = any(
        isinstance(g, (list, tuple, Series, Index, np.ndarray)) for g in keys
    )

    if (
        not any_callable
        and not any_arraylike
        and not any_groupers
        and match_axis_length
        and level is None
    ):
        if isinstance(obj, DataFrame):
            all_in_columns_index = all(
                g in obj.columns or g in obj.index.names for g in keys
            )
        elif isinstance(obj, Series):
            all_in_columns_index = all(g in obj.index.names for g in keys)

        if not all_in_columns_index:
            keys = [com.asarray_tuplesafe(keys)]

    if isinstance(level, (tuple, list)):
        if key is None:
            keys = [None] * len(level)
        levels = level
    else:
        levels = [level] * len(keys)

    groupings = []
    exclusions = []

    def is_label_like(key):
        return (is_bool(key) and key) or isinstance(key, tuple) or key is True

    for g, l in zip(keys, levels):
        obj._check_label_or_level_ambiguity(g)
        if not is_label_like(g):
            try:
                obj._data.get_loc(g)
            except Exception:
                pass
        else:
            exclusions.append(g)
        if isinstance(g, Grouper):
            level = uniq_group_axis.get_level_values(l)
            g = level
        if is_categorical(g) and len(g) != len(obj):
            msg = f"Length of grouper {g} must be the same as index ({len(g)} != {len(obj)})"
            raise ValueError(msg)

        p = Grouping(
            obj.index
            if is_scalar(g) and not isinstance(g, Index)
            else obj[g],
            g,
            obj=obj,
            name=g.name,
            level=l,
            sort=sort,
            observed=observed,
            in_axis=is_label_like(g),
        )
        groupings.append(p)

    if not groupings:
        raise ValueError("No group keys passed!")

    grouper = BaseGrouper(group_axis, groupings, sort=sort, mutated=mutated)
    return grouper, exclusions, obj
