def extract_err(err, data):
    """
    Private function to parse *err* and subtract/add it to *data*.

    Both *err* and *data* are already iterables at this point.
    """
    try:
        a, b = err
        iter(a)
        iter(b)
    except (TypeError, ValueError):
        a = b = err
    a_sh = np.shape(a)
    b_sh = np.shape(b)
    if len(a_sh) > 2 or (len(a_sh) == 2 and a_sh[1] != 1) or len(b_sh) > 2 or (len(b_sh) == 2 and b_sh[1] != 1):
        raise ValueError('err must be a scalar or a 1D or (2, n) array-like')
    if len(a_sh) == 2 or len(b_sh) == 2:
        cbook.warn_deprecated('3.1', message='Support for passing a (n, 1)-shaped error array to errorbar() is deprecated since Matplotlib %(since)s and will be removed %(removal)s; pass a 1D array instead.')
    for e in [a, b]:
        if len(data) != len(e):
            raise ValueError(f'The lengths of the data ({len(data)}) and the error {len(e)} do not match')
    low = [v - e for v, e in zip(data, a)]
    high = [v + e for v, e in zip(data, b)]
    return (low, high)

