def __getstate__(self):
    state = super().__getstate__()
    for key in ['_layoutbox', '_poslayoutbox']:
        state[key] = None
    for grouper_name in ['_shared_x_axes', '_shared_y_axes', '_twinned_axes']:
        grouper = getattr(self, grouper_name)
        state[grouper_name] = grouper.get_siblings(self) if self in grouper else None
    return state

def __setstate__(self, state):
    for grouper_name in ['_shared_x_axes', '_shared_y_axes', '_twinned_axes']:
        siblings = state.pop(grouper_name)
        if siblings:
            getattr(self, grouper_name).join(*siblings)
    self.__dict__ = state
    self._stale = True

def _unstale_viewLim(self):
    scalex = any((ax._stale_viewlim_x for ax in self._shared_x_axes.get_siblings(self)))
    scaley = any((ax._stale_viewlim_y for ax in self._shared_y_axes.get_siblings(self)))
    if scalex or scaley:
        for ax in self._shared_x_axes.get_siblings(self):
            ax._stale_viewlim_x = False
        for ax in self._shared_y_axes.get_siblings(self):
            ax._stale_viewlim_y = False
        self.autoscale_view(scalex=scalex, scaley=scaley)

@property
def viewLim(self):
    self._unstale_viewLim()
    return self._viewLim

def _request_autoscale_view(self, tight=None, scalex=True, scaley=True):
