def _update_image_limits(self, image):
    xmin, xmax, ymin, ymax = image.get_extent()
    self.axes.update_datalim(((xmin, ymin), (xmax, ymax)))

def _update_line_limits(self, line):
    """
    Figures out the data limit of the given line, updating self.dataLim.
    """
    path = line.get_path()
    if path.vertices.size == 0:
        return
    line_trans = line.get_transform()
    if line_trans == self.transData:
        data_path = path
    elif any(line_trans.contains_branch_seperately(self.transData)):
        trans_to_data = line_trans - self.transData
        if self.transData.is_affine:
            line_trans_path = line._get_transformed_path()
            na_path, _ = line_trans_path.get_transformed_path_and_affine()
            data_path = trans_to_data.transform_path_affine(na_path)
        else:
            data_path = trans_to_data.transform_path(path)
    else:
        data_path = path
    if data_path.vertices.size > 0:
        updatex, updatey = line_trans.contains_branch_seperately(self.transData)
        self.dataLim.update_from_path(data_path, self.ignore_existing_data_limits, updatex=updatex, updatey=updatey)
        self.ignore_existing_data_limits = False

def _update_patch_limits(self, patch):
    """update the data limits for patch *p*"""
    if isinstance(patch, mpatches.Rectangle) and (not patch.get_width() and (not patch.get_height())):
        return
    vertices = patch.get_path().vertices
    if vertices.size > 0:
        xys = patch.get_patch_transform().transform(vertices)
        if patch.get_data_transform() != self.transData:
            patch_to_data = patch.get_data_transform() - self.transData
            xys = patch_to_data.transform(xys)
        updatex, updatey = patch.get_transform().contains_branch_seperately(self.transData)
        self.update_datalim(xys, updatex=updatex, updatey=updatey)

def relim(self, visible_only=False):
    """
    Recompute the data limits based on current artists.

    At present, `~.Collection` instances are not supported.

    Parameters
    ----------
    visible_only : bool
        Whether to exclude invisible artists.  Defaults to False.
    """
    self.dataLim.ignore(True)
    self.dataLim.set_points(mtransforms.Bbox.null().get_points())
    self.ignore_existing_data_limits = True
    for line in self.lines:
        if not visible_only or line.get_visible():
            self._update_line_limits(line)
    for p in self.patches:
        if not visible_only or p.get_visible():
            self._update_patch_limits(p)
    for image in self.images:
        if not visible_only or image.get_visible():
            self._update_image_limits(image)

def _process_unit_info(self, xdata=None, ydata=None, kwargs=None):
    """Look for unit *kwargs* and update the axis instances as necessary"""

    def _process_single_axis(data, axis, unit_name, kwargs):
        if axis is None:
            return kwargs
        if data is not None:
            if not axis.have_units():
                axis.update_units(data)
        if kwargs is not None:
            units = kwargs.pop(unit_name, axis.units)
            if self.name == 'polar':
                polar_units = {'xunits': 'thetaunits', 'yunits': 'runits'}
                units = kwargs.pop(polar_units[unit_name], units)
            if units != axis.units:
                axis.set_units(units)
                if data is not None:
                    axis.update_units(data)
        return kwargs
    kwargs = _process_single_axis(xdata, self.xaxis, 'xunits', kwargs)
    kwargs = _process_single_axis(ydata, self.yaxis, 'yunits', kwargs)
    return kwargs

