def _update_title_position(self, renderer):
    """
    Update the title position based on the bounding box enclosing
    all the ticklabels and x-axis spine and xlabel...
    """
    if self._autotitlepos is not None and (not self._autotitlepos):
        _log.debug('title position was updated manually, not adjusting')
        return
    titles = (self.title, self._left_title, self._right_title)
    if self._autotitlepos is None:
        for title in titles:
            x, y = title.get_position()
            if not np.isclose(y, 1.0):
                self._autotitlepos = False
                _log.debug('not adjusting title pos because a title was already placed manually: %f', y)
                return
        self._autotitlepos = True
    for title in titles:
        x, _ = title.get_position()
        title.set_position((x, 1.0))
        axs = self._twinned_axes.get_siblings(self)
        for ax in self.child_axes:
            if ax is not None:
                locator = ax.get_axes_locator()
                if locator:
                    pos = locator(self, renderer)
                    ax.apply_aspect(pos)
                else:
                    ax.apply_aspect()
                axs = axs + [ax]
        top = 0
        for ax in axs:
            if ax.xaxis.get_ticks_position() in ['top', 'unknown'] or ax.xaxis.get_label_position() == 'top':
                bb = ax.xaxis.get_tightbbox(renderer)
            else:
                bb = ax.get_window_extent(renderer)
            if bb is not None:
                top = max(top, bb.ymax)
        if title.get_window_extent(renderer).ymin < top:
            _, y = self.transAxes.inverted().transform((0, top))
            title.set_position((x, y))
            if title.get_window_extent(renderer).ymin < top:
                _, y = self.transAxes.inverted().transform((0.0, 2 * top - title.get_window_extent(renderer).ymin))
                title.set_position((x, y))
    ymax = max((title.get_position()[1] for title in titles))
    for title in titles:
        x, _ = title.get_position()
        title.set_position((x, ymax))

@martist.allow_rasterization
def draw(self, renderer=None, inframe=False):
    """Draw everything (plot lines, axes, labels)"""
    if renderer is None:
        renderer = self.figure._cachedRenderer
    if renderer is None:
        raise RuntimeError('No renderer defined')
    if not self.get_visible():
        return
    self._unstale_viewLim()
    renderer.open_group('axes')
    self._stale = True
    locator = self.get_axes_locator()
    if locator:
        pos = locator(self, renderer)
        self.apply_aspect(pos)
    else:
        self.apply_aspect()
    artists = self.get_children()
    artists.remove(self.patch)
    if not (self.axison and self._frameon):
        for spine in self.spines.values():
            artists.remove(spine)
    self._update_title_position(renderer)
    if not self.axison or inframe:
        for _axis in self._get_axis_list():
            artists.remove(_axis)
    if inframe:
        artists.remove(self.title)
        artists.remove(self._left_title)
        artists.remove(self._right_title)
    if not self.figure.canvas.is_saving():
        artists = [a for a in artists if not a.get_animated() or a in self.images]
    artists = sorted(artists, key=attrgetter('zorder'))
    rasterization_zorder = self._rasterization_zorder
    if rasterization_zorder is not None and artists and (artists[0].zorder < rasterization_zorder):
        renderer.start_rasterizing()
        artists_rasterized = [a for a in artists if a.zorder < rasterization_zorder]
        artists = [a for a in artists if a.zorder >= rasterization_zorder]
    else:
        artists_rasterized = []
    if self.axison and self._frameon:
        self.patch.draw(renderer)
    if artists_rasterized:
        for a in artists_rasterized:
            a.draw(renderer)
        renderer.stop_rasterizing()
    mimage._draw_list_compositing_images(renderer, self, artists)
    renderer.close_group('axes')
    self.stale = False

