def ticklabel_format(self, *, axis='both', style='', scilimits=None, useOffset=None, useLocale=None, useMathText=None):
    """
    Change the `~matplotlib.ticker.ScalarFormatter` used by
    default for linear axes.

    Optional keyword arguments:

      ==============   =========================================
      Keyword          Description
      ==============   =========================================
      *axis*           [ 'x' | 'y' | 'both' ]
      *style*          [ 'sci' (or 'scientific') | 'plain' ]
                       plain turns off scientific notation
      *scilimits*      (m, n), pair of integers; if *style*
                       is 'sci', scientific notation will
                       be used for numbers outside the range
                       10\\ :sup:`m` to 10\\ :sup:`n`.
                       Use (0,0) to include all numbers.
                       Use (m,m) where m <> 0 to fix the order
                       of magnitude to 10\\ :sup:`m`.
      *useOffset*      [ bool | offset ]; if True,
                       the offset will be calculated as needed;
                       if False, no offset will be used; if a
                       numeric offset is specified, it will be
                       used.
      *useLocale*      If True, format the number according to
                       the current locale.  This affects things
                       such as the character used for the
                       decimal separator.  If False, use
                       C-style (English) formatting.  The
                       default setting is controlled by the
                       axes.formatter.use_locale rcparam.
      *useMathText*    If True, render the offset and scientific
                       notation in mathtext
      ==============   =========================================

    Only the major ticks are affected.
    If the method is called when the `~matplotlib.ticker.ScalarFormatter`
    is not the `~matplotlib.ticker.Formatter` being used, an
    `AttributeError` will be raised.
    """
    style = style.lower()
    axis = axis.lower()
    if scilimits is not None:
        try:
            m, n = scilimits
            m + n + 1
        except (ValueError, TypeError):
            raise ValueError('scilimits must be a sequence of 2 integers')
    if style[:3] == 'sci':
        sb = True
    elif style == 'plain':
        sb = False
    elif style == '':
        sb = None
    else:
        raise ValueError('%s is not a valid style value')
    try:
        if sb is not None:
            if axis == 'both' or axis == 'x':
                self.xaxis.major.formatter.set_scientific(sb)
            if axis == 'both' or axis == 'y':
                self.yaxis.major.formatter.set_scientific(sb)
        if scilimits is not None:
            if axis == 'both' or axis == 'x':
                self.xaxis.major.formatter.set_powerlimits(scilimits)
            if axis == 'both' or axis == 'y':
                self.yaxis.major.formatter.set_powerlimits(scilimits)
        if useOffset is not None:
            if axis == 'both' or axis == 'x':
                self.xaxis.major.formatter.set_useOffset(useOffset)
            if axis == 'both' or axis == 'y':
                self.yaxis.major.formatter.set_useOffset(useOffset)
        if useLocale is not None:
            if axis == 'both' or axis == 'x':
                self.xaxis.major.formatter.set_useLocale(useLocale)
            if axis == 'both' or axis == 'y':
                self.yaxis.major.formatter.set_useLocale(useLocale)
        if useMathText is not None:
            if axis == 'both' or axis == 'x':
                self.xaxis.major.formatter.set_useMathText(useMathText)
            if axis == 'both' or axis == 'y':
                self.yaxis.major.formatter.set_useMathText(useMathText)
    except AttributeError:
        raise AttributeError('This method only works with the ScalarFormatter.')

def get_xbound(self):
    """
    Return the lower and upper x-axis bounds, in increasing order.

    See Also
    --------
    set_xbound
    get_xlim, set_xlim
    invert_xaxis, xaxis_inverted
    """
    left, right = self.get_xlim()
    if left < right:
        return (left, right)
    else:
        return (right, left)

def get_xticks(self, minor=False):
    """Return the x ticks as a list of locations"""
    return self.xaxis.get_ticklocs(minor=minor)

def get_ybound(self):
    """
    Return the lower and upper y-axis bounds, in increasing order.

    See Also
    --------
    set_ybound
    get_ylim, set_ylim
    invert_yaxis, yaxis_inverted
    """
    bottom, top = self.get_ylim()
    if bottom < top:
        return (bottom, top)
    else:
        return (top, bottom)

def get_yticks(self, minor=False):
    """Return the y ticks as a list of locations"""
    return self.yaxis.get_ticklocs(minor=minor)

def format_coord(self, x, y):
    """Return a format string formatting the *x*, *y* coordinates."""
    if x is None:
        xs = '???'
    else:
        xs = self.format_xdata(x)
    if y is None:
        ys = '???'
    else:
        ys = self.format_ydata(y)
    return 'x=%s y=%s' % (xs, ys)

def minorticks_on(self):
    """
    Display minor ticks on the axes.

    Displaying minor ticks may reduce performance; you may turn them off
    using `minorticks_off()` if drawing speed is a problem.
    """
    for ax in (self.xaxis, self.yaxis):
        scale = ax.get_scale()
        if scale == 'log':
            s = ax._scale
            ax.set_minor_locator(mticker.LogLocator(s.base, s.subs))
        elif scale == 'symlog':
            s = ax._scale
            ax.set_minor_locator(mticker.SymmetricalLogLocator(s._transform, s.subs))
        else:
            ax.set_minor_locator(mticker.AutoMinorLocator())

def _set_view_from_bbox(self, bbox, direction='in', mode=None, twinx=False, twiny=False):
    """
    Update view from a selection bbox.

    .. note::

        Intended to be overridden by new projection types, but if not, the
        default implementation sets the view limits to the bbox directly.

    Parameters
    ----------
    bbox : 4-tuple or 3 tuple
        * If bbox is a 4 tuple, it is the selected bounding box limits,
            in *display* coordinates.
        * If bbox is a 3 tuple, it is an (xp, yp, scl) triple, where
            (xp,yp) is the center of zooming and scl the scale factor to
            zoom by.

    direction : str
        The direction to apply the bounding box.
            * `'in'` - The bounding box describes the view directly, i.e.,
                       it zooms in.
            * `'out'` - The bounding box describes the size to make the
                        existing view, i.e., it zooms out.

    mode : str or None
        The selection mode, whether to apply the bounding box in only the
        `'x'` direction, `'y'` direction or both (`None`).

    twinx : bool
        Whether this axis is twinned in the *x*-direction.

    twiny : bool
        Whether this axis is twinned in the *y*-direction.
    """
    Xmin, Xmax = self.get_xlim()
    Ymin, Ymax = self.get_ylim()
    if len(bbox) == 3:
        xp, yp, scl = bbox
        if scl == 0:
            scl = 1.0
        if scl > 1:
            direction = 'in'
        else:
            direction = 'out'
            scl = 1 / scl
        tranD2C = self.transData.transform
        xmin, ymin = tranD2C((Xmin, Ymin))
        xmax, ymax = tranD2C((Xmax, Ymax))
        xwidth = xmax - xmin
        ywidth = ymax - ymin
        xcen = (xmax + xmin) * 0.5
        ycen = (ymax + ymin) * 0.5
        xzc = (xp * (scl - 1) + xcen) / scl
        yzc = (yp * (scl - 1) + ycen) / scl
        bbox = [xzc - xwidth / 2.0 / scl, yzc - ywidth / 2.0 / scl, xzc + xwidth / 2.0 / scl, yzc + ywidth / 2.0 / scl]
    elif len(bbox) != 4:
        cbook._warn_external('Warning in _set_view_from_bbox: bounding box is not a tuple of length 3 or 4. Ignoring the view change.')
        return
    lastx, lasty, x, y = bbox
    inverse = self.transData.inverted()
    lastx, lasty = inverse.transform_point((lastx, lasty))
    x, y = inverse.transform_point((x, y))
    if twinx:
        x0, x1 = (Xmin, Xmax)
    elif Xmin < Xmax:
        if x < lastx:
            x0, x1 = (x, lastx)
        else:
            x0, x1 = (lastx, x)
        if x0 < Xmin:
            x0 = Xmin
        if x1 > Xmax:
            x1 = Xmax
    else:
        if x > lastx:
            x0, x1 = (x, lastx)
        else:
            x0, x1 = (lastx, x)
        if x0 > Xmin:
            x0 = Xmin
        if x1 < Xmax:
            x1 = Xmax
    if twiny:
        y0, y1 = (Ymin, Ymax)
    elif Ymin < Ymax:
        if y < lasty:
            y0, y1 = (y, lasty)
        else:
            y0, y1 = (lasty, y)
        if y0 < Ymin:
            y0 = Ymin
        if y1 > Ymax:
            y1 = Ymax
    else:
        if y > lasty:
            y0, y1 = (y, lasty)
        else:
            y0, y1 = (lasty, y)
        if y0 > Ymin:
            y0 = Ymin
        if y1 < Ymax:
            y1 = Ymax
    if direction == 'in':
        if mode == 'x':
            self.set_xlim((x0, x1))
        elif mode == 'y':
            self.set_ylim((y0, y1))
        else:
            self.set_xlim((x0, x1))
            self.set_ylim((y0, y1))
    elif direction == 'out':
        if self.get_xscale() == 'log':
            alpha = np.log(Xmax / Xmin) / np.log(x1 / x0)
            rx1 = pow(Xmin / x0, alpha) * Xmin
            rx2 = pow(Xmax / x0, alpha) * Xmin
        else:
            alpha = (Xmax - Xmin) / (x1 - x0)
            rx1 = alpha * (Xmin - x0) + Xmin
            rx2 = alpha * (Xmax - x0) + Xmin
        if self.get_yscale() == 'log':
            alpha = np.log(Ymax / Ymin) / np.log(y1 / y0)
            ry1 = pow(Ymin / y0, alpha) * Ymin
