        elif key == 'x':
            dy = 0
        elif key == 'y':
            dx = 0
        elif key == 'shift':
            if 2 * abs(dx) < abs(dy):
                dx = 0
            elif 2 * abs(dy) < abs(dx):
                dy = 0
