        dx, dy = format_deltas(key, dx, dy)
        result = p.bbox.translated(-dx, -dy).transformed(p.trans_inverse)
    elif button == 3:
        try:
            dx = -dx / self.bbox.width
            dy = -dy / self.bbox.height
            dx, dy = format_deltas(key, dx, dy)
