        dx = 0
    elif key == 'shift':
        if 2 * abs(dx) < abs(dy):
            dx = 0
        elif 2 * abs(dy) < abs(dx):
            dy = 0
        elif abs(dx) > abs(dy):
            dy = dy / abs(dy) * abs(dx)
        else:
            dx = dx / abs(dx) * abs(dy)
    return dx, dy
