            shading = 'flat'
        C = cbook.safe_masked_invalid(C)
        return (X, Y, C, shading)
    if len(args) == 3:
        C = np.asanyarray(args[2])
        X, Y = [cbook.safe_masked_invalid(a) for a in args[:2]]
        if funcname == 'pcolormesh':
            if np.ma.is_masked(X) or np.ma.is_masked(Y):
                raise ValueError('x and y arguments to pcolormesh cannot have non-finite values or be of type numpy.ma.core.MaskedArray with masked values')
            if isinstance(X, np.ma.core.MaskedArray):
                X = X.data
            if isinstance(Y, np.ma.core.MaskedArray):
                Y = Y.data
        nrows, ncols = C.shape
    else:
        raise TypeError(f'{funcname}() takes 1 or 3 positional arguments but {len(args)} were given')
    Nx = X.shape[-1]
    Ny = Y.shape[0]
    if X.ndim != 2 or X.shape[0] == 1:
        x = X.reshape(1, Nx)
        X = x.repeat(Ny, axis=0)
    if Y.ndim != 2 or Y.shape[1] == 1:
        y = Y.reshape(Ny, 1)
        Y = y.repeat(Nx, axis=1)
    if X.shape != Y.shape:
        raise TypeError('Incompatible X, Y inputs to %s; see help(%s)' % (funcname, funcname))
    if shading == 'auto':
        if ncols == Nx and nrows == Ny:
            shading = 'nearest'
        else:
            shading = 'flat'
    if shading == 'flat':
        if not (ncols in (Nx, Nx - 1) and nrows in (Ny, Ny - 1)):
            raise TypeError('Dimensions of C %s are incompatible with X (%d) and/or Y (%d); see help(%s)' % (C.shape, Nx, Ny, funcname))
