def _to_points(xy1, xy2, slope):
    """
    Check for a valid combination of input parameters and convert
    to two points, if necessary.
    """
    if xy2 is None and slope is None or (xy2 is not None and slope is not None):
        raise TypeError("Exactly one of 'xy2' and 'slope' must be given")
    if xy2 is None:
        x1, y1 = xy1
        xy2 = (x1, y1 + 1) if np.isinf(slope) else (x1 + 1, y1 + slope)
    return (xy1, xy2)

def extract_err(name, err, data):
    """
    Private function to parse *err* and subtract/add it to *data*.

    Both *err* and *data* are already iterables at this point.
    """
    try:
        a, b = err
        iter(a)
        iter(b)
    except (TypeError, ValueError):
        a = b = err
    if np.ndim(a) > 1 or np.ndim(b) > 1:
        raise ValueError(f'{name}err must be a scalar or a 1D or (2, n) array-like')
    for e in [a, b]:
        if len(data) != len(e):
            raise ValueError(f'The lengths of the data ({len(data)}) and the error {len(e)} do not match')
    low = [v - e for v, e in zip(data, a)]
    high = [v + e for v, e in zip(data, b)]
    return (low, high)

