def _get_grouper(obj, key=None, axis=0, level=None, sort=True, observed=False, mutated=False, validate=True):

    """
    create and return a BaseGrouper, which is an internal
    mapping of how to create the grouper indexers.
    This may be composed of multiple Grouping objects, indicating
    multiple groupers

    Groupers are ultimately index mappings. They can originate as:
    index mappings, keys to columns, functions, or Groupers

    Groupers enable local references to axis,level,sort, while
    the passed in axis, level, and sort are 'global'.

    This routine tries to figure out what the passing in references
    are and then creates a Grouping for each one, combined into
    a BaseGrouper.

    If observed & we have a categorical grouper, only show the observed
    values

    If validate, then check for key/level overlaps

    """
    if level is not None:
        if isinstance(obj._get_axis(axis), MultiIndex):
            if pd.api.types.is_list_like(level) and len(level) == 1:
                level = level[0]

            if key is None and pd.api.types.is_scalar(level):
                key = obj._get_axis(axis).get_level_values(level)
                level = None
        else:
            if pd.api.types.is_list_like(level):
                nlevels = len(level)
                if nlevels == 1:
                    level = level[0]
                elif nlevels == 0:
                    raise ValueError("No group keys passed!")
                else:
                    raise ValueError("multiple levels only valid with MultiIndex")

            if isinstance(level, str):
                if obj.index.name != level:
                    raise ValueError("level name {} is not the name of the index".format(level))
            elif level > 0 or level < -1:
                raise ValueError("level > 0 or level < -1 only valid with MultiIndex")

            level = None
            key = obj._get_axis(axis)

    if isinstance(key, pd.core.groupby.grouper.Grouper):
        binner, grouper, obj = key._get_grouper(obj, validate=False)
        if key.key is None:
            return grouper, [], obj
        else:
            return grouper, {key.key}, obj

    elif isinstance(key, pd.core.groupby.grouper.BaseGrouper):
        return key, [], obj

    is_tuple = isinstance(key, tuple)
    all_hashable = is_tuple and pd.api.types.is_hashable(key)

    if is_tuple:
        if (
            all_hashable and key not in obj and set(key).issubset(obj)
        ) or not all_hashable:
            msg = "Interpreting tuple 'by' as a list of keys, rather than " \
                "a single key. Use 'by=[...]' instead of 'by=(...)'. In " \
                "the future, a tuple will always mean a single key."
            warnings.warn(msg, FutureWarning, stacklevel=5)
            key = list(key)

    if not isinstance(key, list):
        keys = [key]
        match_axis_length = False
    else:
        keys = key
        match_axis_length = len(keys) == len(obj._get_axis(axis))

    any_callable = any(callable(g) or isinstance(g, dict) for g in keys)
    any_groupers = any(isinstance(g, type(pd.core.groupby.grouper.Grouper)) for g in keys)
    any_arraylike = any(
        pd.api.types.is_list_like(g) or isinstance(g, (pd.Series, pd.Index, np.ndarray)) for g in keys
    )  # or pd.Series

    if (
        not any_callable
        and not any_arraylike
        and not any_groupers
        and match_axis_length
        and level is None
    ):
        if isinstance(obj, pd.DataFrame):
            all_in_columns_index = all(
                g in obj.columns or g in obj.index.names for g in keys
            )
        elif isinstance(obj, pd.Series):
            all_in_columns_index = all(g in obj.index.names for g in keys)

        if not all_in_columns_index:
            keys = [pd.core.common.arrayasarray_tuplesafe(keys)]

    levels = level if pd.api.types.dtypes.is_list_like(level) else [level] * len(keys)

    groupings = []
    exclusions = []

    for i, (gpr, level) in enumerate(zip(keys, levels)):
        if gpr in obj:
            if validate:
                obj._check_label_or_level_ambiguity(gpr)
            groupings.append(pd.core.groupby.grouper.Grouping(
                obj._get_axis(axis), gpr, obj=obj, name=gpr, level=level, sort=sort,
                observed=observed, in_axis=True
            ))
        # and so on...

        grouper = pd.core.groupby.grouper.BaseGrouper(
            obj._get_axis(axis), groupings, sort=sort, mutated=mutated
        )

        return grouper, exclusions, obj
