def __init__(self, values, categories=None, ordered=None, dtype=None, fastpath=False):
    dtype = CategoricalDtype._from_values_or_dtype(values, categories, ordered, dtype)
    if fastpath:
        self._codes = coerce_indexer_dtype(values, dtype.categories)
        self._dtype = self._dtype.update_dtype(dtype)
        return
    null_mask = np.array(False)
    if is_categorical_dtype(values):
        if dtype.categories is None:
            dtype = CategoricalDtype(values.categories, dtype.ordered)
    elif not isinstance(values, (ABCIndexClass, ABCSeries)):
        values = maybe_infer_to_datetimelike(values, convert_dates=True)
        if not isinstance(values, np.ndarray):
            values = _convert_to_list_like(values)
            if len(values) == 0:
                sanitize_dtype = 'object'
            else:
                sanitize_dtype = None
            null_mask = isna(values)
            if null_mask.any():
                values = [values[idx] for idx in np.where(~null_mask)[0]]
            values = sanitize_array(values, None, dtype=sanitize_dtype)
    if dtype.categories is None:
        try:
            codes, categories = factorize(values, sort=True)
        except TypeError:
            codes, categories = factorize(values, sort=False)
            if dtype.ordered:
                raise TypeError("'values' is not ordered, please explicitly specify the categories order by passing in a categories argument.")
        except ValueError:
            raise NotImplementedError('> 1 ndim Categorical are not supported at this time')
        dtype = CategoricalDtype(categories, dtype.ordered)
    elif is_categorical_dtype(values):
        old_codes = values._values.codes if isinstance(values, ABCSeries) else values.codes
        codes = _recode_for_categories(old_codes, values.dtype.categories, dtype.categories)
    else:
        codes = _get_codes_for_values(values, dtype.categories)
    if null_mask.any():
        full_codes = -np.ones(null_mask.shape, dtype=codes.dtype)
        full_codes[~null_mask] = codes
        codes = full_codes
    self._dtype = self._dtype.update_dtype(dtype)
    self._codes = coerce_indexer_dtype(codes, dtype.categories)

@categories.setter
