    if id_vars is not None:
        if not is_list_like(id_vars):
            id_vars = [id_vars]
        else:
            id_vars = list(id_vars)
            missing = [col for col in id_vars if col not in cols]
            if missing:
                raise KeyError(
                    f"The following 'id_vars' are not present in the DataFrame: {missing}"
                )
