def setitem(self, indexer, value):

    transpose = self.ndim == 2

    if value is None:
        if self.is_numeric:
            value = np.nan

    values = self.values
    if self._can_hold_element(value):
        if lib.is_scalar(value):
            value = convert_scalar(values, value)

    else:
        find_dtype = False

        if hasattr(value, "dtype") and str(value.dtype) == "category":
            dtype = 'object'  # Convert categorical dtype to object dtype
            find_dtype = True
        elif lib.is_scalar(value) and not isna(value):
            dtype, _ = infer_dtype_from_scalar(value, pandas_dtype=True)
            find_dtype = True

        if find_dtype:
            dtype = find_common_type([values.dtype, dtype])
            if not is_dtype_equal(self.dtype, dtype):
                b = self.astype(dtype)
                return b.setitem(indexer, value)

    if is_extension_array_dtype(getattr(value, "dtype", None)):
        arr_value = value
    else:
        arr_value = np.array(value)

    if not self._can_hold_element(value):
        dtype, _ = maybe_promote(arr_value.dtype)
        values = values.astype(dtype)

    if transpose:
        values = values.T

    check_setitem_lengths(indexer, value, values)

    if is_empty_indexer(indexer, arr_value):
        pass

    elif is_scalar_indexer(indexer, arr_value):
        values[indexer] = value

    elif len(arr_value.shape) and arr_value.shape[0] == values.shape[0] and arr_value.size == values.size:
        values[indexer] = value
        try:
            # Convert to object dtype
            values = values.astype('object')
        except ValueError:
            pass

    else:
        values[indexer] = value

    if transpose:
        values = values.T
    block = self.make_block(values)
    return block
