def _partially_consume_prefix(self, prefix, column):
        lines = []
        current_line = ""
        current_column = 0
        wait_for_nl = False
        for char in prefix:
            if wait_for_nl:
                if char == '\n':
                    if len(current_line.expandtabs()) <= column:
                        lines.append(current_line)
                        current_line = ""
                    wait_for_nl = False
            if char == '\t':
                current_column += 4 - (current_column % 4)
            elif char == ' ':
                current_column += 1
            else:
                wait_for_nl = True
            current_line += char

        return ''.join(lines), current_line
