def _dict_arg(self, name, value):
    command = []
    if value and isinstance(value, dict):
        for prop, val in value.items():
            value_pair = '"{0}={1}"'.format(prop, val) if '=' in str(val) else '{0}={1}'.format(prop, val)
            command += [name, value_pair]
    return command
