    if len(clocs) == 0:
        return data

    # Get the MultiIndex from the columns
    if isinstance(data.columns, MultiIndex):
        index = data.columns
    else:
        # If not MultiIndex, use the index
        index = data.index

    # If clocs is a tuple of tuples, convert it to a flat list
