def _partially_consume_prefix(self, prefix, column):
    lines = []
    current_line = ""
    current_column = 0
    wait_for_nl = False
    for char in prefix:
        if not wait_for_nl:
            if char == ' ':
                current_column += 1
            elif char == '\t':
                current_column += 4
            elif char == '\n':
                # unexpected empty line
                current_column = 0
            else:
                # valid character
                current_line += char
                if current_column >= column:
                    lines.append(current_line)
                    current_line = ""
                    current_column = 0
                    wait_for_nl = True
        else:
            if char == '\n':
                # newline encountered after indentation
                wait_for_nl = False
                current_line = ""
                current_column = 0
    return ''.join(lines), prefix[len(''.join(lines)):]
