def _partially_consume_prefix(self, prefix, column):
    lines = []
    current_line = ""
    current_column = 0
    for char in prefix:
        if char == '\n':
            lines.append(current_line + '\n')
            current_line = ""
            current_column = 0
        elif char == ' ':
            current_column += 1
        elif char == '\t':
            current_column += 4
        else:
            if current_column < column:
                current_line += char
            else:
                if not char.isspace():
                    current_line += char
    res = ''.join(lines)
    return res, prefix[len(res):]
