def _partially_consume_prefix(self, prefix, column):
    lines = []
    current_line = ""
    current_column = 0
    for char in prefix:
        if char == ' ':
            current_column += 1
        elif char == '\t':
            current_column += 4
        elif char == '\n':
            current_column = 0
        else:
            if current_column >= column:
                return ''.join(lines), current_line + prefix[len(current_line):]
            current_line += char
    return ''.join(lines), current_line
