def astype_nansafe(arr, dtype, copy: bool = True, skipna: bool = False):
    """
    Cast the elements of an array to a given dtype a nan-safe manner.

    Parameters
    ----------
    arr : ndarray
    dtype : np.dtype
    copy : bool, default True
        If False, a view will be attempted but may fail, if
        e.g. the item sizes don't align.
    skipna: bool, default False
        Whether or not we should skip NaN when casting as a string-type.

    Raises
    ------
    ValueError
        The dtype was a datetime64/timedelta64 dtype, but it had no unit.
    """

    # dispatch on extension dtype if needed
    if hasattr(dtype, 'construct_array_type'):
        return dtype.construct_array_type()._from_sequence(arr, dtype=dtype, copy=copy)

    if not isinstance(dtype, np.dtype):
        dtype = pd.CategoricalDtype(dtype)

    if issubclass(dtype.type, str):
        return pd.core.algorithms.astype_object(arr.ravel())

    elif isinstance(arr.dtype, pd.DatetimeTZDtype):
        if dtype == 'object':
            return pd.to_datetime(arr.ravel()).values
        elif dtype == 'int64':
            return arr.view(dtype)

        if dtype.kind == "M":
            return arr.astype(dtype)

        raise TypeError(f"cannot astype a datetimelike from [{arr.dtype}] to [{dtype}]")

    elif isinstance(arr.dtype, pd.TimedeltaTZDtype):
        if dtype == 'object':
            return pd.to_timedelta(arr.ravel()).values
        elif dtype == 'int64':
            return arr.view(dtype)

        if dtype.kind == "m":
            mask = pd.isna(arr)
            result = arr.astype(dtype).astype(np.float64)
            result[mask] = np.nan
            return result
        elif dtype == np.dtype('m'):
            return arr.astype(dtype, copy=copy)

        raise TypeError(f"cannot astype a timedelta from [{arr.dtype}] to [{dtype}]")

    elif np.issubdtype(arr.dtype, np.floating) and np.issubdtype(np.dtype(dtype), np.integer):

        if not np.isfinite(arr).all():
            raise ValueError("Cannot convert non-finite values (NA or inf) to integer")

    elif pd.api.types.is_object_dtype(arr):

        if np.issubdtype(np.dtype(dtype), np.integer):
            return pd.core.algorithms.astype_object(arr)

        elif isinstance(dtype, pd.DatetimeTZDtype):
            return astype_nansafe(pd.to_datetime(arr).values, dtype, copy=copy)
        elif isinstance(dtype, pd.TimedeltaTZDtype):
            return astype_nansafe(pd.to_timedelta(arr).values, dtype, copy=copy)

    if str(dtype) in ("datetime64", "timedelta64"):
        msg = (
            f"The '{dtype}' dtype has no unit. Please pass in "
            f"'{dtype}[ns]' instead."
        )
        raise ValueError(msg)

    if copy or pd.api.types.is_object_dtype(arr) or pd.api.types.is_object_dtype(dtype):
        # Explicit copy, or required since NumPy can't view from / to object.
        return arr.astype(dtype, copy=True)

    return arr.view(dtype)
