def pivot_table(
    data,
    values=None,
    index=None,
    columns=None,
    aggfunc="mean",
    fill_value=None,
    margins=False,
    dropna=True,
    margins_name="All",
    observed=False,
) -> "DataFrame":
    if not isinstance(data, pd.DataFrame):
        raise TypeError("Input 'data' is not a pandas DataFrame")

    if columns is not None and not isinstance(columns, (list, tuple)):
        columns = [columns]

    if not isinstance(values, (list, tuple)) and values is not None:
        values = [values]

    if not is_list_like(columns):
        columns = [columns]

    keys = index + columns

    result = None

    if not values:
        values = data.columns
        for key in keys:
            try:
                values = values.drop(key)
            except (TypeError, ValueError, KeyError):
                pass
        values = list(values)

    grouped = data.groupby(keys, observed=observed)
    table = grouped.agg(aggfunc)

    if not dropna and isinstance(table, ABCDataFrame) and len(table.columns):
        table = table.dropna(how="all")

    if fill_value is not None:
        table = table._ensure_type(table.fillna(fill_value, downcast="infer"))

    if margins:
        if dropna:
            data = data[data.notna().all(axis=1)]
            result = _add_margins(
                table,
                data,
                values,
                rows=index,
                cols=columns,
                aggfunc=aggfunc,
                observed=dropna,
                margins_name=margins_name,
                fill_value=fill_value,
            )

    if values and len(index) == 0 and len(columns) > 0:
        table = table.T

    if result is not None:
        return result
    else:
        return table
