def astype_nansafe(arr, dtype, copy: bool = True, skipna: bool = False):
    """
    Cast the elements of an array to a given dtype in a nan-safe manner.

    Parameters
    ----------
    arr : ndarray
    dtype : np.dtype
    copy : bool, default True
        If False, a view will be attempted but may fail, if
        e.g. the item sizes don't align.
    skipna: bool, default False
        Whether or not we should skip NaN when casting as a string-type.

    Raises
    ------
    ValueError
        The dtype was a datetime64/timedelta64 dtype, but it had no unit.
    """

    # dispatch on extension dtype if needed
    if is_extension_array_dtype(dtype):
        return dtype.construct_array_type()._from_sequence(arr, dtype=dtype, copy=copy)

    if not isinstance(dtype, np.dtype):
        dtype = np.dtype(dtype)

    if issubclass(dtype.type, str):
        return lib.astype_str(arr.ravel(), skipna=skipna).reshape(arr.shape)

    elif np.issubdtype(dtype, np.datetime64):
        if np.issubdtype(arr.dtype, np.datetime64) and arr.dtype != dtype:
            arr = np.array(arr, dtype=dtype)
        if dtype.itemsize < arr.dtype.itemsize:
            if copy:
                arr = arr.copy()
            arr.view(dtype)
        return arr

    elif np.issubdtype(dtype, np.timedelta64):
        if np.issubdtype(arr.dtype, np.timedelta64) and arr.dtype != dtype:
            arr = np.array(arr, dtype=dtype)
        if dtype.itemsize < arr.dtype.itemsize:
            if copy:
                arr = arr.copy()
            arr.view(dtype)
        return arr

    if np.issubdtype(arr.dtype, np.floating) and np.issubdtype(dtype, np.integer):
        if not np.isfinite(arr).all():
            raise ValueError("Cannot convert non-finite values (NA or inf) to integer")

    if np.issubdtype(arr.dtype, np.object_) and np.issubdtype(dtype, np.integer):
        if np.issubdtype(dtype, np.integer) and np.issubdtype(arr.dtype, np.object_):
            return arr.view(dtype)
        elif np.issubdtype(dtype, np.datetime64) and np.issubdtype(arr.dtype, np.object_):
            return np.array(pd.to_datetime(arr)).view(dtype)
        elif np.issubdtype(dtype, np.timedelta64) and np.issubdtype(arr.dtype, np.object_):
            return np.array(pd.to_timedelta(arr)).view(dtype)

    if dtype.itemsize < arr.dtype.itemsize:
        if copy:
            arr = arr.copy()
        arr.view(dtype)

    return arr
